INSERT INTO `product_translation` (`id`, `translatable_id`, `title`, `short_desc`, `description`, `locale`, `how_to_use`, `ingredient`, `sub_title`)
VALUES
	(1, 1, 'Moistbalance Lotion', 'ACSEINE Moist Balance Lotion', '<p>เราไม่เพียงแต่ยึดติดกับ &quot;คุณภาพ&quot; ของความชุ่มชื้นแต่เรายังใส่ใจใน &quot;ปริมาณ&quot; อย่างละเอียดเพื่อให้เป็นโลชั่นที่ให้ความชุ่มชื่นเพื่อตอบสนองต่อปัญหาผิวแห้งของผู้หญิงสมัยใหม่</p>\r\n\r\n<p>ทุกช่วงเวลาที่เนื้อโลชั่นสัมผัสผิว จะให้ความรู้สึกเหมือนแทรกซึมเข้าสู่ผิวของคุณ โดยเนื้อโลชั่นละเอียด ไม่รู้สึกเหนียว แต่ให้ความรู้สึกชุ่มชื้นอย่างต่อเนื่อง ผิวไม่รู้จักความแห้งกร้านได้ตลอดทั้งวัน</p>', 'en', NULL, NULL, '(Lotion)'),
	(2, 1, 'Moistbalance Lotion', NULL, '<p>เราไม่เพียงแต่ยึดติดกับ &quot;คุณภาพ&quot; ของความชุ่มชื้นแต่เรายังใส่ใจใน &quot;ปริมาณ&quot; อย่างละเอียดเพื่อให้เป็นโลชั่นที่ให้ความชุ่มชื่นเพื่อตอบสนองต่อปัญหาผิวแห้งของผู้หญิงสมัยใหม่</p>\r\n\r\n<p>ทุกช่วงเวลาที่เนื้อโลชั่นสัมผัสผิว จะให้ความรู้สึกเหมือนแทรกซึมเข้าสู่ผิวของคุณ โดยเนื้อโลชั่นละเอียด ไม่รู้สึกเหนียว แต่ให้ความรู้สึกชุ่มชื้นอย่างต่อเนื่อง ผิวไม่รู้จักความแห้งกร้านได้ตลอดทั้งวัน</p>', 'th', 'หลังจากการล้างหน้า เทโลชั่นปริมาณเหรียญ 10 บาท 2-3 ครั้ง และทาให้ทั่วใบหน้าและลำคอ', NULL, '(โลชั่น)'),
	(3, 2, 'Moistbalance Gel', NULL, '<p>แม้ว่าความชุ่มชื้นอาจจะไม่ส่งผลต่อเนื่องและทันทีที่เริ่มดูแลผิวที่หยาบกร้าน. แต่เราติดตามความรู้สึกที่สบายจากการใช้ , การแทรกซึมเข้าสู่ผิวอย่างล้ำลึก , ผลลัพธ์ที่ดีเยี่ยมจากความชุ่มชื้น มุ่งเน้นดูแลและเพิ่มความชุ่มชื้นให้ผิว</p>\r\n\r\n<p>เมื่อเจลเนื้อนุ่มสัมผัสลงบนผิวจะแทรกซึมและละลายเข้าสู่ผิว โดยให้ผิวรู้สึกสบายและสดชื่น ยาวนานตลอดทั้งวัน</p>', 'en', '-', '-', '(Gel moisturizer)'),
	(4, 2, 'Moistbalance Gel', NULL, '<p>แม้ว่าความชุ่มชื้นอาจจะไม่ส่งผลต่อเนื่องและทันทีที่เริ่มดูแลผิวที่หยาบกร้าน. แต่เราติดตามความรู้สึกที่สบายจากการใช้ , การแทรกซึมเข้าสู่ผิวอย่างล้ำลึก , ผลลัพธ์ที่ดีเยี่ยมจากความชุ่มชื้น มุ่งเน้นดูแลและเพิ่มความชุ่มชื้นให้ผิว</p>\r\n\r\n<p>เมื่อเจลเนื้อนุ่มสัมผัสลงบนผิวจะแทรกซึมและละลายเข้าสู่ผิว โดยให้ผิวรู้สึกสบายและสดชื่น ยาวนานตลอดทั้งวัน</p>', 'th', 'ล้างหน้าให้สะอาด จากนั้นใช้เจลในปริมาณเท่าเม็ดไข่มุก 2 -3 เม็ด ทาให้ทั่วบริเวณใบหน้าไปจนถึงลำคอ', NULL, '(เจล)'),
	(5, 3, 'Super Sunshield EX SPF50+ PA++++', NULL, NULL, 'en', NULL, NULL, NULL),
	(6, 3, 'Super Sunshield EX SPF50+ PA++++', NULL, '<p>ครีมกันแดดประสิทธิภาพสูงสุดของ ACSEINE ช่วยแก้ปัญหาสีผิวที่ดูไม่สม่ำเสมอ ผิวจึงกระจ่างใสอย่างเป็นธรรมชาติ มาพร้อมเนื้อครีมแบบเบส อ่อนนุ่ม สบายผิว แห้งไว ไม่เหนอะหนะ พิสูจน์แล้วว่าอ่อนโยนแม้แต่กับผิวบอบบางแพ้ง่าย</p>\r\n\r\n<p>Super Sunshield EX SPF50+ PA++++<br />\r\n22g 1,300 บาท (รวมภาษี)</p>', 'th', 'ปริมาณที่เหมาะสมคือ 1-2 ปั๊ม\r\nหลังดูแลผิวพรรณตามปกติเรียบร้อยแล้วทาให้ทั่วใบหน้า\r\nและสามารถล้างออกด้วยน้ำยาทำความสะอาดทั่วไป', 'WATER, CYCLOPENTASILOXANE, ZINC OXIDE, TITANIUM DIOXIDE, HYDROGENATED POLYISOBUTENE, BUTYLENE GLYCOL, PEG/PPG-19/19 DIMETHICONE, JOJOBA ESTERS, PEG-40, SODIUM CHLORIDE, FRAGARIA ANANASSA (STRAWBERRY) SEED EXTRACT, ERGOTHIONEINE, DIPOTASSIUM GLYCYRRHIZATE, STEARYL GLYCYRRHETINATE, GENTIANA LUTEA ROOT EXTRACT, THEANINE, ASCORBYL TETRAISOPALMITATE, ROSA MULTIFLORA FRUIT EXTRACT, SODIUM HYALURONATE, LAMINARIA OCHROLEUCA EXTRACT, DIMETHICONE/VINYL DIMETHICONE CROSSPOLYMER, PEG-12 DIMETHICONE, POTASSIUM CHLORIDE, HYDROGENATED STARCH HYDROLYSATE, ALUMINUM HYDROXIDE, BORON NITRIDE, POLYGLYCERYL-2 ISOSTEARATE, ALUMINUM STARCH OCTENYLSUCCINATE, GLYCOSYL TREHALOSE, GLYCERIN, CITRULLINE, SILICA, DISTEARDIMONIUM HECTORITE, ALUMINUM DIMYRISTATE, DIMETHICONE, STEARIC ACID, CAPRYLIC/CAPRIC TRIGLYCERIDE, TRIETHOXYSILYLETHYL POLYDIMETHYLSILOXYETHYL HEXYL DIMETHICONE, HYDROGEN DIMETHICONE, DEXTRIN PALMITATE, SUCROSE PENTAERUCATE, MICA, LAURYL PEG-9 POLYDIMETHYLSILOXYETHYL DIMETHICONE, POTASSIUM PHOSPHATE, SODIUM PHOSPHATE, DISODIUM EDTA, TOCOPHEROL, PHENOXYETHANOL, METHYLPARABEN, IRON OXIDES', NULL),
	(11, 6, 'Facial Soap AD', NULL, '<p>ฟองน้ำเนื้อละเอียดนุ่มนวลอ่อนโยนต่อผิวบอบบาง ล้างสิ่งสกปรกที่คุณไม่ต้องการทิ้งไป<br />\r\nไม่มีส่วนผสมของกรดโอลิอิกและกรด lauric (กรดไขมันที่ทำให้ผิวระคายเคือง)<br />\r\nสบู่นี้ช่วยคงความชุ่มชื้นที่ผิวแห้งต้องการขณะขจัดสิ่งสกปรก</p>\r\n\r\n<p>ปราศจากน้ำหอม ไม่มีสีเทียม ปลอดสาร paraben (สารกันเสีย)<br />\r\nไม่มี แอลกอฮอล์ (เอทานอล)<br />\r\n(ไม่รับประกันว่าจะไม่ทำให้เกิดปัญหาผิวในทุกคน.)<br />\r\n&nbsp;</p>', 'th', '1.【นวดโฟมให้แน่นฝ่ามือ】\r\nการล้างหน้าโดยใช้โฟมจะเป็นสิ่งที่ช่วยปกคลุมสิ่งสกปรก โฟมทำหน้าที่เป็นเบาะรองพื้นเพื่อปกป้องผิว\r\n\r\n2.【ถูอย่างเบามือ】\r\nกุญแจสำคัญคือการห่อหุ้มใบหน้าด้วยโฟมโดยใช้ฝ่ามือและล้างออกเบา ๆ\r\n\r\n3.【ล้างด้วยน้ำอุ่นประมาณ 20 ครั้ง 】\r\nถ้าไม่ล้างให้เพียงพอสารลดแรงตึงผิวบางตัวจะยังคงอยู่ให้ค่า pH ของผิวมีแนวโน้มเป็นด่างชั่วคราวและปล่อยให้มีความเสี่ยงต่อสารระคายเคืองแต่การล้างมากเกินไปจะทำให้ชั้นความชื้นสูญหายเช่นกัน', 'WATER, SODIUM MYRISTATE, SUCROSE, SORBITOL, MALTITOL, SODIUM PALMITATE, SODIUM ISOSTEARATE, GLYCERIN, POTASSIUM MYRISTATE, PPG-20, DIGLYCERIN, SODIUM STEARATE, POTASSIUM PALMITATE, POTASSIUM ISOSTEARATE, POTASSIUM STEARATE, SODIUM CHLORIDE, TETRASODIUM ETIDRONATE', NULL),
	(12, 6, 'Facial Soap AD', NULL, '<h3>Its fine-textured lather is gentle on sensitive skin.&nbsp;<br />\r\nThoroughly removes only the impurities you don&rsquo;t want to leave behind.</h3>\r\n\r\n<p>We cut the oleic acid and lauric acid (fatty acids that irritate the skin).&nbsp;<br />\r\nThis soap keeps in place the moisture that dry skin needs, while removing impurities.<br />\r\nSoap ingredients wash the skin gently and don&rsquo;t stay behind on the skin.</p>\r\n\r\n<ul>\r\n	<li>Hypoallergenic, perfume-free, no artificial color, paraben (preservative)-free,<br />\r\n	alcohol (ethanol)-free<br />\r\n	(This does not guarantee that it will not cause skin problems in anyone.)</li>\r\n</ul>', 'en', '1.【Lather well in the palm of your hand】\r\nThe face wash turns into a foam that envelops impurities and removes them.\r\nWhen not lathered up enough, its cleansing ability declines.\r\nThe foam also serves as a cushion to protect the skin.\r\n\r\n2.【Do not rub hard】\r\nThe key is to envelop the face in foam using the palms of the hands, and wash gently.\r\nThe hands tend to gravitate toward the cheeks and wash them too much, so just rub lightly there.\r\n\r\n3.【Rinse with lukewarm water about 20 times(*)】\r\nIf not rinsed enough, some surfactant agent will remain, giving skin\'s pH an alkaline tendency temporarily and leaving it vulnerable to irritants.\r\nAnd too much rinsing will cause the moisture layer to be lost.', 'WATER, SODIUM MYRISTATE, SUCROSE, SORBITOL, MALTITOL, SODIUM PALMITATE, SODIUM ISOSTEARATE, GLYCERIN, POTASSIUM MYRISTATE, PPG-20, DIGLYCERIN, SODIUM STEARATE, POTASSIUM PALMITATE, POTASSIUM ISOSTEARATE, POTASSIUM STEARATE, SODIUM CHLORIDE, TETRASODIUM ETIDRONATE', NULL),
	(13, 7, 'Control Lotion AD', NULL, '<p>เติมความชุ่มชื่นให้แก่ผิว<br />\r\nคืนความชุ่มชื่นให้ผิวที่บอบบาง<br />\r\nโลชั่นลดอาการแพ้ง่ายที่ช่วยเพิ่มความชุ่มชื้นกับผิว สำหรับผิวแห้งที่เสี่ยงต่อการระคายเคือง ให้ความชุ่มชื่นสูงช่วยคืนความชุ่มชื้นให้กับชั้นผิวหนัง ทำให้ผิวพรรณสามารถทนต่อการซึมผ่านได้ดีขึ้นหากได้รับสารระคายเคือง</p>\r\n\r\n<p>ปราศจากน้ำหอมปราศ &nbsp;ปราศจากสารปรอท สาร paraben (สารกันบูด) ปราศจากแอลกอฮอล์&nbsp;<br />\r\n(ไม่รับประกันว่าจะไม่ทำให้เกิดปัญหาผิวในทุกคน.)<br />\r\n&nbsp;</p>', 'th', 'ใช้ในปริมาณเหรียญ 5 บาท 1 – 2 เหรียญ เช้า-เย็นหลังจากอาบน้ำ', 'WATER, GLYCERIN, BUTYLENE GLYCOL, CITRIC ACID, SODIUM CITRATE, PHENOXYETHANOL', NULL),
	(14, 7, 'Control Lotion AD', NULL, '<h3>Generously replenishes the moisture skin lacks.&nbsp;<br />\r\nRestores dewy moisture to sensitive skin that&rsquo;s frequently dry.</h3>\r\n\r\n<p>Hypoallergenic lotion that infuses moisture to serve as a barrier for dry skin that&rsquo;s vulnerable to irritation. Highly moisturizing, it restores dewy moisture to the stratum corneum, leaving skin more resistant to penetration by irritants.</p>\r\n\r\n<ul>\r\n	<li>Hypoallergenic, perfume-free, no artificial color, surfactant-free, paraben (preservative)-free, alcohol (ethanol)-free (This does not guarantee that it will not cause skin problems in anyone.)</li>\r\n</ul>', 'en', 'An appropriate amount is about the size of 1-2 large coins. Use morning and night after washing the face.', 'WATER, GLYCERIN, BUTYLENE GLYCOL, CITRIC ACID, SODIUM CITRATE, PHENOXYETHANOL', NULL),
	(15, 8, 'Control Essence AD', NULL, '<p>ควบคุมผิวจากความแห้งกร้าน<br />\r\nปกป้องผิวหนังที่บอบบางด้วยน้ำยาเคลือบป้องกันน้ำ<br />\r\nปราศจากน้ำมันและปกป้องผิวจากความแห้งกร้าน</p>\r\n\r\n<p>โพลิเมอร์ในรูปแบบเป็นเสื้อป้องกันน้ำที่อยู่ด้านบนของชั้นผิวหนัง ที่ตอบสนองการทำงานของเยื่อแผ่นไขมันซึ่งมีแนวโน้มที่จะหายไป เนื้อไม่เหนียวเหนอะหนะช่วยให้ผิวนุ่มและแห้งกร้าน ประกอบด้วยโซเดียมไฮยาลูโรเนท (เป็นส่วนประกอบในการเก็บความชุ่มชื้น)</p>\r\n\r\n<p>ปราศจากน้ำหอมปราศ &nbsp;ปราศจากสารปรอท สาร paraben (สารกันบูด) ปราศจากแอลกอฮอล์&nbsp;<br />\r\n(ไม่รับประกันว่าจะไม่ทำให้เกิดปัญหาผิวในทุกคน.)<br />\r\n&nbsp;</p>', 'th', 'กดเนื้อครีมประมาณ 2 -3 ครั้ง บำรุงผิวเช้า-เย็นหลังจากใช้โลชั่น', 'WATER, GLYCERIN, BUTYLENE GLYCOL, SORBITOL, SODIUM HYALURONATE, PEG-23M, CITRIC ACID, SODIUM CITRATE, PHENOXYETHANOL', NULL),
	(16, 8, 'Control Essence AD', NULL, '<h3>Guards sensitive skin with an aqueous protective coat.&nbsp;<br />\r\nOil-free and comfortable, it protects skin from dryness.</h3>\r\n\r\n<p>Polymers in the essence form an aqueous protective coat on top of the stratum corneum that fulfills the functions of the sebum membrane, which tends to disappear. With a non-sticky, lightweight texture, it envelops skin and wards off dryness. Contains sodium hyaluronate (a moisture-retaining ingredient).</p>\r\n\r\n<ul>\r\n	<li>Hypoallergenic, perfume-free, no artificial color, surfactant-free, paraben (preservative)-free, alcohol (ethanol)-free (This does not guarantee that it will not cause skin problems in anyone.)</li>\r\n</ul>', 'en', 'An appropriate amount is 2-3 pumps’ worth. Use morning and night after applying lotion.', 'WATER, GLYCERIN, BUTYLENE GLYCOL, SORBITOL, SODIUM HYALURONATE, PEG-23M, CITRIC ACID, SODIUM CITRATE, PHENOXYETHANOL', NULL),
	(17, 9, 'Milky Gel AD', NULL, '<p>เนื้อบางเบาเหมาะสำหรับผิวบอบบางแพ้ง่ายสำหรับบำรุงผิวตอนเช้า<br />\r\nโลชั่นเพิ่มความชุ่มชื้น เจลไม่มีน้ำมัน&nbsp;<br />\r\nลื่นเบา ไม่มีความรู้สึกเหนียว ขั้นตอนสุดท้ายในการดูแลผิวตอนเช้า&nbsp;</p>\r\n\r\n<p>ปราศจากน้ำหอมปราศ &nbsp;ปราศจากสารปรอท สาร paraben (สารกันบูด) ปราศจากแอลกอฮอล์&nbsp;<br />\r\n(ไม่รับประกันว่าจะไม่ทำให้เกิดปัญหาผิวในทุกคน.)<br />\r\n&nbsp;</p>', 'th', 'ปริมาณที่เหมาะสมบีบเนื้อครีม 1-2 ครั้ง ใช้ในตอนเช้าหลังจากทาโลชั่นและผลิตภัณฑ์ดูแลผิวอื่น ๆ  แต่เมื่อผิวต้องการความชุ่มชื้นก็สามารถใช้ได้ตามต้องการ *สามารถใช้ในเวลากลางคืนได้', 'WATER, GLYCERIN, BUTYLENE GLYCOL, ALLANTOIN, SODIUM HYALURONATE, SODIUM HYDROXIDE, CARBOMER, XANTHAN GUM, PHENOXYETHANOL, TITANIUM DIOXIDE', NULL),
	(18, 9, 'Milky Gel AD', NULL, '<h3>With a light texture that&rsquo;s ideal for moisturizing sensitive skin in the morning.&nbsp;<br />\r\nGel-type moisturizing lotion doesn&#39;t include oil or emulsifiers.</h3>\r\n\r\n<p>Glides on lightly like an emulsion, with no sticky feeling. Use at the end of your morning skin care. Forms an aqueous protective coat on the skin&#39;s surface to guard the skin throughout the day.</p>\r\n\r\n<ul>\r\n	<li>Hypoallergenic, perfume-free, no artificial color, surfactant-free, paraben (preservative)-free, alcohol (ethanol)-free (This does not guarantee that it will not cause skin problems in anyone.)</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Volume&nbsp;60ml</li>\r\n	<li>Price&nbsp;3,780 yen (before tax 3,500 yen)</li>\r\n</ul>', 'en', 'An appropriate amount is 1-2 pumps’ worth. Use in the morning after applying lotion and essence, etc. *When you want to really moisturize well, use one pump\'s worth more than the indicated amount. *Can also be used at night.', 'WATER, GLYCERIN, BUTYLENE GLYCOL, ALLANTOIN, SODIUM HYALURONATE, SODIUM HYDROXIDE, CARBOMER, XANTHAN GUM, PHENOXYETHANOL, TITANIUM DIOXIDE', NULL),
	(19, 10, 'Skin Protector AD', NULL, '<p style=\"margin-top:0pt; margin-bottom:0pt; margin-left:0in; text-align:left\">ให้ความชุ่มชื่นนี้ช่วยขจัดไขมันส่วนเกินบนผิวหน้าใช้เมื่อผิวมีความอ่อนไหวเป็นพิเศษหรือเมื่อแห้งกร้าน&nbsp;&nbsp;ปกป้องผิวจากความหมองคล้ำ&nbsp;<br />\r\nปราศจากน้ำหอมปราศ &nbsp;ปราศจากสารปรอท สาร paraben (สารกันบูด) ปราศจากแอลกอฮอล์&nbsp;<br />\r\n(ไม่รับประกันว่าจะไม่ทำให้เกิดปัญหาผิวในทุกคน.)<br />\r\n&nbsp;</p>', 'th', 'ใช้ดูแลผิวในขั้นตอนสุดท้าย หรือใช้เฉพาะจุดที่รู้สึกแห้งเป็นพิเศษ', 'PETROLATUM, OCTYLDODECYL MYRISTATE, SQUALANE, MICROCRYSTALLINE WAX, ORYZANOL, TOCOPHEROL, CHOLESTERYL/OCTYLDODECYL LAUROYL GLUTAMATE, HYDROGENATED LECITHIN, CHOLESTEROL, PHENOXYETHANOL', NULL),
	(20, 10, 'Skin Protector AD', NULL, '<h3>Highly protective petrolatum base. This moisturizing cream shores up the sebum membrane, which tends to disappear in sensitive skin.</h3>\r\n\r\n<p>Use when skin is especially sensitive, or when dryness is a concern. With a refined petroleum jelly base, it contains orizanol, which protects skin. Shores up the sebum membrane and keeps skin healthy.</p>\r\n\r\n<ul>\r\n	<li>Hypoallergenic, perfume-free, no artificial color, surfactant-free, paraben (preservative)-free, alcohol (ethanol)-free (This does not guarantee that it will not cause skin problems in anyone.)</li>\r\n</ul>', 'en', 'Use at the end of your skin care routine, especially on areas where dryness is a concern.', 'PETROLATUM, OCTYLDODECYL MYRISTATE, SQUALANE, MICROCRYSTALLINE WAX, ORYZANOL, TOCOPHEROL, CHOLESTERYL/OCTYLDODECYL LAUROYL GLUTAMATE, HYDROGENATED LECITHIN, CHOLESTEROL, PHENOXYETHANOL', NULL),
	(21, 11, 'Body Shampoo AD', NULL, '<p>ใช้ทุกวันสำหรับผิวบอบบาง<br />\r\nประกอบด้วยส่วนผสมทำความสะอาดที่ไม่ตกค้าง<br />\r\nล้างออกได้อย่างง่ายดาย ไม่ทิ้งสารตกค้างในที่ผิว &nbsp;<br />\r\nปราศจากน้ำหอมปราศ &nbsp;ปราศจากสารปรอท สาร paraben (สารกันบูด) ปราศจากแอลกอฮอล์&nbsp;<br />\r\n(ไม่รับประกันว่าจะไม่ทำให้เกิดปัญหาผิวในทุกคน.)<br />\r\n&nbsp;</p>', 'th', NULL, NULL, NULL),
	(22, 11, 'Body Shampoo AD', NULL, '<h3>Use every day on sensitive skin. Contains non-residual cleansing ingredients.</h3>\r\n\r\n<p>Rinses off easily, and the soap ingredients don&rsquo;t stay behind on the skin, so it keeps skin clean without imposing a burden on it.</p>\r\n\r\n<ul>\r\n	<li>Hypoallergenic, perfume-free, no artificial color, paraben (preservative)-free, alcohol (ethanol)-free (This does not guarantee that it will not cause skin problems in anyone.)</li>\r\n</ul>', 'en', NULL, NULL, NULL),
	(23, 12, 'Shield Water AD', NULL, '<p style=\"margin-top:0pt; margin-bottom:0pt; margin-left:0in; text-align:left\">โล่ชั่นชนิดน้ำปกป้องผิว<br />\r\nโลชั่นบำรุงผิวกายอุดมไปด้วยโซเดียมไฮยาลูโรเนต (เป็นส่วนประกอบที่ช่วยรักษาความชุ่มชื้น)<br />\r\nคืนค่าความเป็นกรด - ด่างของผิวที่บอบบางให้กับกรดอ่อนที่อ่อนแอ<br />\r\nน้ำที่เป็นกรดนี้ช่วยคืนความชุ่มชื่นให้กับผิว &nbsp;ช่วยคืนความแข็งแรงของผิว &nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin-top:0pt; margin-bottom:0pt; margin-left:0in; text-align:left\">ปราศจากน้ำหอมปราศ &nbsp;ปราศจากสารปรอท สาร paraben (สารกันบูด) ปราศจากแอลกอฮอล์&nbsp;<br />\r\n(ไม่รับประกันว่าจะไม่ทำให้เกิดปัญหาผิวในทุกคน.)<br />\r\n&nbsp;</p>', 'th', 'สำหรับบริเวณที่มีปัญหาเรื่องความแห้งกร้าน  ระยะห่างจากผิว 20 ซม. ในการฉีด', 'WATER, GLYCERIN, BUTYLENE GLYCOL, SODIUM HYALURONATE, PEG-23M, CALCIUM CHLORIDE, POTASSIUM CHLORIDE, PHOSPHORIC ACID, TRISODIUM PHOSPHATE, PHENOXYETHANOL', NULL),
	(24, 12, 'Shield Water AD', NULL, '<h3>Acidic water that restores healthy skin.</h3>\r\n\r\n<p>Full-body moisturizing lotion containing sodium hyaluronate (a moisture-retaining ingredient). Restores sensitive skin&rsquo;s pH to a healthy weak acidity. This acidic water restores dewy moisture to dry skin.</p>\r\n\r\n<ul>\r\n	<li>Hypoallergenic, perfume-free, no artificial color, surfactant-free, paraben (preservative)-free, alcohol (ethanol)-free (This does not guarantee that it will not cause skin problems in anyone.)</li>\r\n</ul>', 'en', 'For areas where dryness is a concern.　Hold about 20cm from skin and spray it on.', 'WATER, GLYCERIN, BUTYLENE GLYCOL, SODIUM HYALURONATE, PEG-23M, CALCIUM CHLORIDE, POTASSIUM CHLORIDE, PHOSPHORIC ACID, TRISODIUM PHOSPHATE, PHENOXYETHANOL', NULL),
	(25, 13, 'Hair Care Shampoo', NULL, '<p>แชมพูดูแลเส้นผม<br />\r\nด้วยส่วนผสมของสารทำความสะอาดที่ไม่ตกค้าง กรดอะมิโนช่วยให้เส้นผมสะอาดและชุ่มชื้น<br />\r\nเหมาะสำหรับผิวที่บอบบางนี้ แชมพูที่เป็นกรดไม่รุนแรง มีสารทำความสะอาดที่ไม่เป็นกรดอะมิโนที่ไม่ตกค้างและหลีกเลี่ยงปัญหาการแพ้บริเวณลำคอและใบหน้า โฟมเนื้อละเอียดผสานกับหนังศีรษะซึ่งมีแนวโน้มที่จะแห้งและคัน ขจัดสิ่งสกปรกออกจากเส้นผมและหนังศีรษะให้สะอาด&nbsp;</p>\r\n\r\n<p>ปราศจากน้ำหอมปราศ &nbsp;ปราศจากสารปรอท สาร paraben (สารกันบูด) ปราศจากแอลกอฮอล์&nbsp;<br />\r\n(ไม่รับประกันว่าจะไม่ทำให้เกิดปัญหาผิวในทุกคน.)<br />\r\n&nbsp;</p>', 'th', NULL, 'WATER, SODIUM LAUROYL METHYLAMINOPROPIONATE, LAURAMIDE DEA, CETEARETH-60 MYRISTYL GLYCOL, CITRIC ACID, COCAMIDOPROPYL BETAINE, POLYQUATERNIUM-10, DISODIUM EDTA, PHENOXYETHANOL', NULL),
	(26, 13, 'Hair Care Shampoo', NULL, '<h3>Made with non-residual cleansing ingredients. Amino acid-based, it leaves hair clean and hydrated.</h3>\r\n\r\n<p>Made for sensitive skin, this weakly acidic shampoo contains non-residual, amino acid-based cleansing ingredients, with an eye to avoiding trouble on the neck and face. Mild, fine-textured foam cares for the scalp, which tends to suffer from dryness and itching, while thoroughly removing impurities to leave the hair and scalp hydrated and clean.</p>\r\n\r\n<ul>\r\n	<li>Hypoallergenic, perfume-free, no artificial color, paraben (preservative)-free, alcohol (ethanol)-free (This does not guarantee that it will not cause skin problems in anyone.)</li>\r\n</ul>', 'en', NULL, 'WATER, SODIUM LAUROYL METHYLAMINOPROPIONATE, LAURAMIDE DEA, CETEARETH-60 MYRISTYL GLYCOL, CITRIC ACID, COCAMIDOPROPYL BETAINE, POLYQUATERNIUM-10, DISODIUM EDTA, PHENOXYETHANOL', NULL),
	(27, 14, 'Hair Care Conditioner', NULL, '<p>ครีมบำรุงผม<br />\r\nด้วยส่วนผสม AHA (แลคติคแอซิด) ทำให้เส้นผมเป็นประกายจากภายใน<br />\r\nทำมาเพื่อผิวบอบบางด้วยความระมัดระวังในการระคายเคืองไม่เพียงแต่หนังศีรษะ แต่ยังรวมถึงบริวเวณลำคอและใบหน้า &nbsp;ให้ความชุ่มชื่นได้ดี ล้างคราบสารตกค้างทั้งหมดที่เป็นสาเหตุของปัญหาผิว ช่วยให้เส้นผมชุ่มชื่นเงางามและอ่อนนุ่ม</p>\r\n\r\n<p>ปราศจากน้ำหอมปราศ &nbsp;ปราศจากสารปรอท สาร paraben (สารกันบูด) ปราศจากแอลกอฮอล์&nbsp;<br />\r\n(ไม่รับประกันว่าจะไม่ทำให้เกิดปัญหาผิวในทุกคน.)<br />\r\n&nbsp;</p>', 'th', 'หลังจากสระผมให้ใช้ปริมาณที่เหมาะสม', 'WATER, MYRISTYL ALCOHOL, GLYCERIN, BEHENYL ALCOHOL, DIMETHYL STEARAMINE, GLYCERYL STEARATE, ISOPROPYL PALMITATE, DIPROPYLENE GLYCOL, LACTIC ACID, DIISOBUTYL ADIPATE, OLEA EUROPAEA (OLIVE) FRUIT OIL, HYDROGENATED PALM OIL, PEG-60 HYDROGENATED CASTOR OIL, C10-30 CHOLESTEROL/LANOSTEROL ESTERS, SODIUM HYDROXIDE, HYDROGENATED SOYBEAN OIL, OLEYL ALCOHOL, HYDROGENATED POLY(C6-12 OLEFIN), PHENOXYETHANOL', NULL),
	(28, 14, 'Hair Care Conditioner', NULL, '<h3>With the conditioning ingredient AHA (lactic acid), it makes hair lustrous from the inside out.</h3>\r\n\r\n<p>Made for sensitive skin, with care given to prevent irritating not only the scalp but also the neck and face, etc. Leaves moisture in place yet cleanses thoroughly, rinsing away all residue that might cause skin problems. AHA (a conditioning ingredient) leaves hair hydrated, lustrous, and silky.</p>\r\n\r\n<ul>\r\n	<li>Hypoallergenic, perfume-free, no artificial color, paraben (preservative)-free, alcohol (ethanol)-free (This does not guarantee that it will not cause skin problems in anyone.)</li>\r\n</ul>', 'en', 'After shampooing, get an appropriate amount in the palm of your hand, work it into your hair, and then rinse it out. (When hair is damaged, leave it on a while before rinsing.)', 'WATER, MYRISTYL ALCOHOL, GLYCERIN, BEHENYL ALCOHOL, DIMETHYL STEARAMINE, GLYCERYL STEARATE, ISOPROPYL PALMITATE, DIPROPYLENE GLYCOL, LACTIC ACID, DIISOBUTYL ADIPATE, OLEA EUROPAEA (OLIVE) FRUIT OIL, HYDROGENATED PALM OIL, PEG-60 HYDROGENATED CASTOR OIL, C10-30 CHOLESTEROL/LANOSTEROL ESTERS, SODIUM HYDROXIDE, HYDROGENATED SOYBEAN OIL, OLEYL ALCOHOL, HYDROGENATED POLY(C6-12 OLEFIN), PHENOXYETHANOL', NULL),
	(29, 15, 'Skin Care Program AD', NULL, '<p>Basic Skin Care&rdquo; Series ชุดล้างหน้าและโลชั่น</p>\r\n\r\n<p>เมื่อชั้นผิวหนังอยู่ในความระส่ำระสายและผิวมีแนวโน้มที่จะเกิดการระคายเคืองต่อเครื่องสำอางและความหยาบกร้านเนื่องจากการเปลี่ยนแปลงของฤดูกาล ฯลฯ&nbsp; ชุดล้างหน้าและโลชั่นนี้จะช่วยมันจะรักษาสภาพผิวและลบเลือนริ้วรอย</p>\r\n\r\n<p>(เพียงพอสำหรับการทดลองใช้เต็ม 2-3 สัปดาห์)</p>\r\n\r\n<p>ล้างหน้า : ครีมโฟม (N) 30g</p>\r\n\r\n<p>โลชั่น : โลชั่นบำรุงผิว (I) 60ml</p>\r\n\r\n<p>และยังมาพร้อมกับน้ำยาล้างเครื่องสำอาง (Mild Wash Cleansing) และ Emollient Conditioner</p>', 'th', NULL, NULL, NULL),
	(30, 15, 'Skin Care Program AD', NULL, '<h3>&quot;AD Control&quot; series Trial set of face wash and lotion.</h3>\r\n\r\n<p>With minimal ingredients and simple steps, it wards off problems and makes hair healthier.</p>\r\n\r\n<p><br />\r\n＜Enough for a full 2-3 week trial＞<br />\r\n【Face wash】&nbsp;Facial Soap AD&nbsp;30g<br />\r\n【Lotion】&nbsp;AD Control Lotion&nbsp;60ml<br />\r\n<br />\r\nAlso comes with<br />\r\nmakeup remover (Cleansing Oil) and essence (AD Control Essence) samples.</p>', 'en', NULL, NULL, NULL),
	(31, 16, 'Super Sunshield BRIGHT VEIL SPF50+PA++++', NULL, '<p>Super Sunshield BRIGHT VEIL SPF50 + PA ++++<br />\r\nปกป้องผิวจากแสงแดด สัมผัสนุ่ม สบาย เพิ่มความสดใส ขจัดความหมองคล้ำและสีผิวไม่สม่ำเสมอ<br />\r\nด้วยประสิทธิภาพการป้องกันรังสี UV สูงสุดของ Acseine &nbsp;ครีมกันแดดนี้ยังช่วยให้รู้สึกสบายและนุ่มนวล&nbsp;<br />\r\nช่วยยืดอายุการทำงานของผิว&nbsp;</p>\r\n\r\n<p>ไม่มีสารเคมีชนิดที่ทำโดยไม่ต้องดูดซับรังสีอัลตราไวโอเลตใด ๆ<br />\r\nทำด้วยเทคโนโลยี PV (Perfect Veil) ดังนั้นสีย้อมไม่สัมผัสผิวโดยตรง<br />\r\nชนิดกันน้ำ (สามารถล้างออกโดยคลีนซิ่งทั่วไป)<br />\r\nปราศจากน้ำหอมปราศ &nbsp;ปราศจากสารปรอท สาร paraben (สารกันบูด) ปราศจากแอลกอฮอล์&nbsp;<br />\r\n(ไม่รับประกันว่าจะไม่ทำให้เกิดปัญหาผิวในทุกคน.)<br />\r\n&nbsp;</p>', 'th', 'บีบครีม 1 – 2 ครั้งต่อการใช้งาน\r\nหลังจากเตรียมผิวด้วยการดูแลผิวพรรณตามปกติแล้วทาให้ทั่วหน้าทั่วใบหน้า\r\n(สามารถล้างออกโดยคลีนซิ่งทั่วไป)', 'WATER, CYCLOPENTASILOXANE, ZINC OXIDE, TITANIUM DIOXIDE, GLYCERIN, HYDROGENATED POLYISOBUTENE, BUTYLENE GLYCOL, PEG/PPG-19/19 DIMETHICONE, JOJOBA ESTERS, PEG-40, SODIUM CHLORIDE, ERGOTHIONEINE, DIPOTASSIUM GLYCYRRHIZATE, STEARYL GLYCYRRHETINATE, GENTIANA LUTEA ROOT EXTRACT, THEANINE, ASCORBYL TETRAISOPALMITATE, ROSA MULTIFLORA FRUIT EXTRACT, SODIUM HYALURONATE, LITCHI CHINENSIS SEED EXTRACT, LAMINARIA OCHROLEUCA EXTRACT, DIMETHICONE/VINYL DIMETHICONE CROSSPOLYMER, PEG-12 DIMETHICONE, POTASSIUM CHLORIDE, HYDROGENATED STARCH HYDROLYSATE, ALUMINUM HYDROXIDE, BORON NITRIDE, POLYGLYCERYL-2 ISOSTEARATE, ALUMINUM STARCH OCTENYLSUCCINATE, GLYCOSYL TREHALOSE, CITRULLINE, SILICA, DISTEARDIMONIUM HECTORITE, ALUMINUM DIMYRISTATE, DIMETHICONE, STEARIC ACID, CAPRYLIC/CAPRIC TRIGLYCERIDE, TRIETHOXYSILYLETHYL POLYDIMETHYLSILOXYETHYL HEXYL DIMETHICONE, HYDROGEN DIMETHICONE, DEXTRIN PALMITATE, SUCROSE PENTAERUCATE, MICA, LAURYL PEG-9 POLYDIMETHYLSILOXYETHYL DIMETHICONE, POTASSIUM PHOSPHATE, SODIUM PHOSPHATE, DISODIUM EDTA, TOCOPHEROL, PHENOXYETHANOL, IRON OXIDES', NULL),
	(32, 16, 'Super Sunshield BRIGHT VEIL SPF50+PA++++', NULL, '<h3>A veil that covers the skin in dewy comfort.&nbsp;<br />\r\nBrightens and corrects dullness and uneven coloring.</h3>\r\n\r\n<p>SPF50+・PA++++<br />\r\nWith Acseine&#39;s highest UV blocking effect ever,<br />\r\nthis sunscreen base cream is also uncompromisingly comfortable and gentle.&nbsp;<br />\r\nThe dewy, moist feeling lasts, supporting skin&rsquo;s barrier function.&nbsp;<br />\r\nCorrects dullness and uneven coloring to create a bright,<br />\r\nbeautiful complexion.</p>\r\n\r\n<ul>\r\n	<li>Non-chemical type made without any UV ray absorbent</li>\r\n	<li>Made using PV (Perfect Veil) technology, so dyes don&#39;t touch the skin directly.</li>\r\n	<li>Waterproof type (can be removed with ordinary cleanser)</li>\r\n	<li>Hypoallergenic, perfume-free, alcohol (ethanol)-free, paraben-free,<br />\r\n	non-comedogenic tested(This does not guarantee that it will not cause skin problems<br />\r\n	and acne (comedo) in anyone.)</li>\r\n</ul>', 'en', 'An appropriate amount is 1-2 pumps’ worth. \r\nAfter preparing skin with your regular skin care, spread evenly over the entire face. \r\nCan be removed with ordinary cleanser.', 'WATER, CYCLOPENTASILOXANE, ZINC OXIDE, TITANIUM DIOXIDE, GLYCERIN, HYDROGENATED POLYISOBUTENE, BUTYLENE GLYCOL, PEG/PPG-19/19 DIMETHICONE, JOJOBA ESTERS, PEG-40, SODIUM CHLORIDE, ERGOTHIONEINE, DIPOTASSIUM GLYCYRRHIZATE, STEARYL GLYCYRRHETINATE, GENTIANA LUTEA ROOT EXTRACT, THEANINE, ASCORBYL TETRAISOPALMITATE, ROSA MULTIFLORA FRUIT EXTRACT, SODIUM HYALURONATE, LITCHI CHINENSIS SEED EXTRACT, LAMINARIA OCHROLEUCA EXTRACT, DIMETHICONE/VINYL DIMETHICONE CROSSPOLYMER, PEG-12 DIMETHICONE, POTASSIUM CHLORIDE, HYDROGENATED STARCH HYDROLYSATE, ALUMINUM HYDROXIDE, BORON NITRIDE, POLYGLYCERYL-2 ISOSTEARATE, ALUMINUM STARCH OCTENYLSUCCINATE, GLYCOSYL TREHALOSE, CITRULLINE, SILICA, DISTEARDIMONIUM HECTORITE, ALUMINUM DIMYRISTATE, DIMETHICONE, STEARIC ACID, CAPRYLIC/CAPRIC TRIGLYCERIDE, TRIETHOXYSIL